package CodeforcesRound732;
import java.util.*;
public class encryption {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc= new Scanner(System.in);
		int tc= sc.nextInt();
		while(tc-->0)
		{
			int p= sc.nextInt();
			System.out.println(2+" "+(p-1));
		}
	}

}
